import{_ as t,o,c as r,Q as l}from"./chunks/framework.879a0371.js";const X=JSON.parse('{"title":"3D变换预处理","description":"","frontmatter":{},"headers":[],"relativePath":"operation/3d/3D变换预处理.md","filePath":"operation/3d/3D变换预处理.md"}'),n={name:"operation/3d/3D变换预处理.md"},i=l('<h1 id="_3d变换预处理" tabindex="-1">3D变换预处理 <a class="header-anchor" href="#_3d变换预处理" aria-label="Permalink to &quot;3D变换预处理&quot;">​</a></h1><h2 id="算子功能" tabindex="-1">算子功能 <a class="header-anchor" href="#算子功能" aria-label="Permalink to &quot;算子功能&quot;">​</a></h2><p>对输入的点云进行仿射变换，输出变换后的点云</p><h2 id="算子输入参数" tabindex="-1">算子输入参数 <a class="header-anchor" href="#算子输入参数" aria-label="Permalink to &quot;算子输入参数&quot;">​</a></h2><h3 id="变换模式" tabindex="-1">变换模式 <a class="header-anchor" href="#变换模式" aria-label="Permalink to &quot;变换模式&quot;">​</a></h3><ul><li><strong>平移</strong><ul><li><strong>平移X</strong>：沿着X轴平移的距离，正数代表向X轴正方向平移；负数代表向负方向平移。</li><li><strong>平移Y</strong>：沿着Y轴平移的距离，正数代表向Y轴正方向平移；负数代表向负方向平移。</li><li><strong>平移Z</strong>：沿着Z轴平移的距离，正数代表向Z轴正方向平移；负数代表向负方向平移。</li><li><strong>反转平移</strong>：若勾选，向相反方向平移。</li></ul></li><li><strong>旋转</strong><ul><li><strong>绕X轴旋转</strong>：正数代表围绕X轴逆时针旋转；负数代表围绕X轴顺时针旋转。</li><li><strong>绕Y轴旋转</strong>：正数代表围绕Y轴逆时针旋转；负数代表围绕Y轴顺时针旋转。</li><li><strong>绕Z轴旋转</strong>：正数代表围绕Z轴逆时针旋转；负数代表围绕Z轴顺时针旋转。</li><li><strong>反转旋转</strong>：若勾选，向相反方向进行旋转。</li></ul></li><li><strong>缩放</strong><ul><li><strong>缩放X</strong>：对X坐标进行缩放，负数代表对镜像后的点云X坐标进行缩放。</li><li><strong>缩放Y</strong>：对Y坐标进行缩放，负数代表对镜像后的点云Y坐标进行缩放。</li><li><strong>缩放Z</strong>：对Z坐标进行缩放，负数代表对镜像后的点云Z坐标进行缩放。</li><li><strong>翻转缩放</strong>：对输入的点云进行镜像后，再进行缩放。</li></ul></li><li><strong>斜切</strong><ul><li><strong>斜切轴</strong>：沿着选定的坐标轴方向对点云进行斜拉。 <ul><li><strong>根据Y斜切X</strong>：根据点云Y坐标值对X坐标进行变换</li><li><strong>根据Z斜切X</strong>：根据点云Z坐标值对X坐标进行变换</li><li><strong>根据X斜切Y</strong>：根据点云X坐标值对Y坐标进行变换</li><li><strong>根据Z斜切Y</strong>：根据点云Z坐标值对Y坐标进行变换</li><li><strong>根据X斜切Z</strong>：根据点云X坐标值对Z坐标进行变换</li><li><strong>根据Y斜切Z</strong>：根据点云Y坐标值对Z坐标进行变换</li></ul></li><li><strong>斜切单位</strong>：对某一坐标进行变换时的比例因子量纲 <ul><li><strong>原始斜切因子</strong>：无量纲</li><li><strong>角度斜切因子</strong>：输入值单位为度</li></ul></li><li><strong>反转斜切</strong>：沿着选定的坐标轴的相反方向对点云进行斜切</li></ul></li><li><strong>镜像</strong><ul><li><strong>X</strong>：将点云关于X轴进行镜像</li><li><strong>Y</strong>：将点云关于Y轴进行镜像</li><li><strong>Z</strong>：将点云关于Z轴进行镜像</li></ul></li></ul>',6),s=[i];function a(g,e,_,d,u,c){return o(),r("div",null,s)}const Y=t(n,[["render",a]]);export{X as __pageData,Y as default};
