import{_ as t,o as e,c as o,Q as a}from"./chunks/framework.879a0371.js";const u=JSON.parse('{"title":"3D平面度","description":"","frontmatter":{},"headers":[],"relativePath":"operation/3d/3D平面度.md","filePath":"operation/3d/3D平面度.md"}'),r={name:"operation/3d/3D平面度.md"},n=a('<h1 id="_3d平面度" tabindex="-1">3D平面度 <a class="header-anchor" href="#_3d平面度" aria-label="Permalink to &quot;3D平面度&quot;">​</a></h1><h2 id="算子功能" tabindex="-1">算子功能 <a class="header-anchor" href="#算子功能" aria-label="Permalink to &quot;算子功能&quot;">​</a></h2><p>采用平均值做点的方式，每一个区域中计算出一个平均点，利用平均点拟合出评定基面。计算出峰基平面度偏差和基谷平面度偏差，最终的平面度=峰基平面度偏差-基谷平面度偏差</p><blockquote><p><strong>参考</strong>：<a href="https://www.iso.org/obp/ui/en/#iso:std:iso:12781:-1:ed-1:v1:en:fig:1" target="_blank" rel="noreferrer">ISO 12781-1:2011(en) Geometrical product specifications (GPS) — Flatness — Part 1: Vocabulary and parameters of flatness</a></p></blockquote><h2 id="算子输入参数" tabindex="-1">算子输入参数 <a class="header-anchor" href="#算子输入参数" aria-label="Permalink to &quot;算子输入参数&quot;">​</a></h2><ul><li><p><strong>输入图像</strong>：图像来源的寄存器编号</p></li><li><p><strong>区域数量</strong>：设置检测区域的数量，最少为3个，且要求不共线</p></li><li><p><strong>区域</strong>：从寄存器获取区域或手动设置区域</p></li><li><p><strong>使用百分位数滤波</strong>：</p><ul><li><strong>概述</strong>：若启用，将对每个区域中的点进行百分位数滤波</li><li><strong>低百分比</strong>：根据输入的百分比计算z方向的低阈值，去除低于此阈值的所有点</li><li><strong>高百分比</strong>：根据输入的百分比计算z方向的高阈值，去除高于此阈值的所有点</li></ul></li></ul>',6),s=[n];function i(l,_,c,d,p,h){return e(),o("div",null,s)}const f=t(r,[["render",i]]);export{u as __pageData,f as default};
