import{_ as t,o,c as r,Q as l}from"./chunks/framework.6ed370c1.js";const h=JSON.parse('{"title":"3D方形探针","description":"","frontmatter":{},"headers":[],"relativePath":"operation/3d/3D方形探针.md","filePath":"operation/3d/3D方形探针.md","lastUpdated":1695870664000}'),n={name:"operation/3d/3D方形探针.md"},i=l('<h1 id="_3d方形探针" tabindex="-1">3D方形探针 <a class="header-anchor" href="#_3d方形探针" aria-label="Permalink to &quot;3D方形探针&quot;">​</a></h1><h2 id="算子功能" tabindex="-1">算子功能 <a class="header-anchor" href="#算子功能" aria-label="Permalink to &quot;算子功能&quot;">​</a></h2><p>在特定的方向上搜索边缘点，返回点、直线、平面、区域等几何参数。</p><h2 id="算子输入参数" tabindex="-1">算子输入参数 <a class="header-anchor" href="#算子输入参数" aria-label="Permalink to &quot;算子输入参数&quot;">​</a></h2><ul><li><p><strong>输入图像</strong>：图像来源的寄存器编号</p></li><li><p><strong>区域</strong>：设置搜索区域的范围，有手动模式和从寄存器读取两种方式。</p></li><li><p><strong>仅探测前景</strong>：是否只搜索前景边缘点，若勾选，则需要手动设置z方向阈值，以此来分离前景和背景。</p></li><li><p><strong>搜索方向</strong></p><ul><li><strong>方向</strong>： <ul><li><strong>+X</strong>：沿着X轴正方向搜索</li><li><strong>-X</strong>：沿着X轴负方向搜索</li><li><strong>+Y</strong>：沿着Y轴正方向搜索</li><li><strong>-Y</strong>：沿着Y轴负方向搜索</li><li><strong>+Z</strong>：沿着Z轴正方向搜索</li><li><strong>-Z</strong>：沿着Z轴负方向搜索</li><li><strong>选择所有点</strong>：选择ROI内所有点</li></ul></li><li><strong>排除锥角</strong>：沿着选定方向搜索时的圆锥角度，圆锥以外的点将会被保留。</li></ul></li><li><p><strong>分辨率</strong>：沿着选定方向搜索时的步长倒数，增大分辨率意味着采样点个数的增加，但同时会增加算子的运行时间。</p></li><li><p><strong>输出几何</strong>：指定输出的几何参数</p><ul><li><strong>点</strong>：点云区域的质心坐标点</li><li><strong>线</strong>：包含直线段的中心点和直线的方向向量</li><li><strong>3D平面</strong>：包含平面的几何中心点和平面的法向量</li></ul></li><li><p><strong>滤波方式</strong></p><ul><li><strong>无滤波</strong>：不进行找到的边缘点进行滤波</li><li><strong>统计滤波</strong>： <ul><li><strong>概述</strong>：该滤波方式先对找到的边缘点进行一次最佳拟合，接着利用标准差乘积因子，计算得到距离阈值。计算所有边缘点到拟合模型的距离，若该距离大于距离阈值，则被认为是离群点。</li><li><strong>标准差乘积</strong>：推荐值范围[1.0, 2.0]</li></ul></li><li><strong>RANSAC</strong>: <ul><li><strong>概述</strong>：该滤波方式分为假设和检验两个步骤。假设流程即从点集中任取n个点进行几何模型拟合；检验流程即利用剩下的点集计算其到该几何模型的距离，小于距离阈值的点被称为局内点，若局内点的个数满足阈值，则此次假设流程得出的几何模型为最佳模型，停止迭代。</li><li><strong>距离阈值</strong>：若某一点到输出几何的距离大于此阈值，将会被滤除。</li><li><strong>迭代次数</strong>：超过此迭代次数，算法将停止迭代。</li></ul></li></ul></li></ul>',5),s=[i];function a(g,e,_,p,d,c){return o(),r("div",null,s)}const m=t(n,[["render",a]]);export{h as __pageData,m as default};
