import{_ as o,o as t,c as e,Q as s}from"./chunks/framework.6ed370c1.js";const r="/docs/assets/点云转深度_3D点云.fa6c66f8.png",a="/docs/assets/2D深度图.83e03a46.png",D=JSON.parse('{"title":"3D点云转深度图","description":"","frontmatter":{},"headers":[],"relativePath":"operation/3d/3D点云转深度图.md","filePath":"operation/3d/3D点云转深度图.md","lastUpdated":1705368759000}'),i={name:"operation/3d/3D点云转深度图.md"},n=s('<h1 id="_3d点云转深度图" tabindex="-1">3D点云转深度图 <a class="header-anchor" href="#_3d点云转深度图" aria-label="Permalink to &quot;3D点云转深度图&quot;">​</a></h1><h2 id="算子功能" tabindex="-1">算子功能 <a class="header-anchor" href="#算子功能" aria-label="Permalink to &quot;算子功能&quot;">​</a></h2><p>将输入的3D点云图转换成2D深度图。将每个点的z值映射为2D深度图中的灰度值。</p><p><img src="'+r+'" alt="3D"><img src="'+a+'" alt="2D"></p><h2 id="算子输入参数" tabindex="-1">算子输入参数 <a class="header-anchor" href="#算子输入参数" aria-label="Permalink to &quot;算子输入参数&quot;">​</a></h2><ul><li><strong>输入图像</strong>：图像来源的寄存器编号</li><li><strong>自动检测窗口</strong>：若选中，则自动计算需要转换的点云范围，此范围将是<em>点云图外接包围盒的范围</em>；若取消选中，则会出现<code>区域</code>选项进行人为设置待转换区域。</li><li><strong>区域</strong>：此选项仅在<code>自动检测窗口</code>取消选中时生效。此时，需要人为设定转换区域。</li><li><strong>插值</strong>：暂未提供插值方法</li><li>位深度：可以选择<code>8位深度图</code>或者<code>16位深度图</code>。其中，<code>8位深度图</code>对应的灰度值范围是[0, 255]，<code>16位深度图</code>对应的灰度值范围是[0, 65534]</li><li><strong>线性转换范围</strong>： <ul><li><strong>自动检测Z值范围</strong>：若选中，则自动将当前转换区域中的最大高度值与黑色像素相对应，最小高度值与白色像素相对应。</li><li><strong>黑色像素对应的高度值</strong>：当<code>自动检测Z值范围</code>取消选中时，需要设置此项。</li><li><strong>白色像素对应的高度值</strong>：当<code>自动检测Z值范围</code>取消选中时，需要设置此项。</li></ul></li><li><strong>深度图分辨率</strong><ul><li><strong>自动检测分辨率</strong>：若选中，则将<code>X方向</code>和<code>Y方向</code>的分辨率都设置为0.1</li><li><strong>X方向分辨率</strong>：若<code>自动检测分辨率</code>取消选中，则此项需要人为设置</li><li><strong>Y方向分辨率</strong>：若<code>自动检测分辨率</code>取消选中，则此项需要人为设置</li></ul></li><li><strong>输出图像</strong>：图像输出的寄存器编号</li></ul>',6),l=[n];function c(d,_,g,p,h,m){return t(),e("div",null,l)}const f=o(i,[["render",c]]);export{D as __pageData,f as default};
